-----------------------------------------------------描述: 活动公告界面--时间: 2012.9.28--作者: Guosen---------------------------------------------------ActivityNoticeUI = {}local p = ActivityNoticeUI;---------------------------------------------------local ID_BTN_CLOSE			= 533;	-- Xlocal ID_LABEL_NOTICE		= 14;	-- 公告标签local szTitle	= "1月15號更新公告";local szNotice	= "【活動】:首次充值大優惠\n只要是第一次充值就可以領取大禮包\n充值10金可以領取超過150金禮包\n【活動】:沖級奪寶大比拼！\n開服一周後等級排名前十的玩家可以\n獲得大量獎勵\n【活動】:千金賞萬戶侯！\n開服一周後聲望排名前十的玩家可以\n獲得大量獎勵\n更多活动详情具體詳情\n請點擊“儲值禮包”查看\n或者點擊左上按鈕“群組”查看";-----------------------------------------------------function p.ShowUI()	LogInfo( "ActivityNoticeUI: ShowUI" );	local scene = GetSMGameScene();	if not CheckP(scene) then	LogInfo( "ActivityNoticeUI: ShowUI failed! scene is nil" );		return false;	end		local layer = createNDUILayer();	if not CheckP(layer) then		LogInfo( "ActivityNoticeUI: ShowUI failed! layer is nil" );		return false;	end		layer:SetPopupDlgFlag( true );	layer:Init();	layer:SetTag( NMAINSCENECHILDTAG.ActivityNoticeUI );	layer:SetFrameRect( RectFullScreenUILayer );	scene:AddChildZ( layer, 1 );	local uiLoad = createNDUILoad();	if ( nil == uiLoad ) then		layer:Free();		LogInfo( "ActivityNoticeUI: ShowUI failed! uiLoad is nil" );		return false;	end	uiLoad:Load( "activity.ini", layer, p.OnUIEventActivityNoticeUI, 0, 0 );	uiLoad:Free();		--	local pLabelNotice = GetLabel( layer, ID_LABEL_NOTICE );	if ( pLabelNotice ~= nil ) then		pLabelNotice:SetText( szNotice );	end	end---------------------------------------------------function p.CloseUI()	local scene = GetSMGameScene();	if ( scene ~= nil ) then		scene:RemoveChildByTag( NMAINSCENECHILDTAG.ActivityNoticeUI, true );	endend---------------------------------------------------function p.OnUIEventActivityNoticeUI( uiNode, uiEventType, param )	local tag = uiNode:GetTag();	if ( uiEventType == NUIEventType.TE_TOUCH_BTN_CLICK ) then        if ( ID_BTN_CLOSE == tag ) then        	p.CloseUI();        end    end    return true;end