-----------------------------------------------------描述: 活动公告界面--时间: 2012.9.28--作者: Guosen---------------------------------------------------ActivityNoticeUI = {}local p = ActivityNoticeUI;---------------------------------------------------local ID_BTN_CLOSE			= 533;	-- Xlocal ID_LABEL_NOTICE		= 14;	-- 公告标签local szTitle	= "10月18号更新公告";local szNotice	= "挑战世界BOSS：\n30级开启，每天15:30对战魔神吕布，21:30对战妖道于吉，斩妖除魔，看谁才是真正的英雄！\n\n全服大乱斗：\n40级开启，每天20:30尽情激战，\n龙狼卫vs虎豹骑，看谁能笑傲群雄！\n\n军团功能开启：\n30级以上玩家都可以创建军团，为你自己的军团招兵买马吧！\n\n玩家交流QQ群：257951863";-----------------------------------------------------function p.ShowUI()	LogInfo( "ActivityNoticeUI: ShowUI" );	local scene = GetSMGameScene();	if not CheckP(scene) then	LogInfo( "ActivityNoticeUI: ShowUI failed! scene is nil" );		return false;	end		local layer = createNDUILayer();	if not CheckP(layer) then		LogInfo( "ActivityNoticeUI: ShowUI failed! layer is nil" );		return false;	end	layer:Init();	layer:SetTag( NMAINSCENECHILDTAG.ActivityNoticeUI );	layer:SetFrameRect( RectFullScreenUILayer );	scene:AddChildZ( layer, 1 );	local uiLoad = createNDUILoad();	if ( nil == uiLoad ) then		layer:Free();		LogInfo( "ActivityNoticeUI: ShowUI failed! uiLoad is nil" );		return false;	end	uiLoad:Load( "activity.ini", layer, p.OnUIEventActivityNoticeUI, 0, 0 );	uiLoad:Free();		--	local pLabelNotice = GetLabel( layer, ID_LABEL_NOTICE );	if ( pLabelNotice ~= nil ) then		pLabelNotice:SetText( szNotice );	end	end---------------------------------------------------function p.CloseUI()	local scene = GetSMGameScene();	if ( scene ~= nil ) then		scene:RemoveChildByTag( NMAINSCENECHILDTAG.ActivityNoticeUI, true );	endend---------------------------------------------------function p.OnUIEventActivityNoticeUI( uiNode, uiEventType, param )	local tag = uiNode:GetTag();	if ( uiEventType == NUIEventType.TE_TOUCH_BTN_CLICK ) then        if ( ID_BTN_CLOSE == tag ) then        	p.CloseUI();        end    end    return true;end