-----------------------------------------------------描述: 活动公告界面--时间: 2012.9.28--作者: Guosen---------------------------------------------------ActivityNoticeUI = {}local p = ActivityNoticeUI;---------------------------------------------------local ID_BTN_CLOSE			= 533;	-- Xlocal ID_LABEL_NOTICE		= 14;	-- 公告标签local szTitle	= "12月7号更新公告";local szNotice	= "1、4服《名扬四海》火热开启，12大开服活动期待你的参与，详情请关注官方论坛活动专贴\n\n2、论坛开启方式：“设置”-“游戏论坛”按钮\n\n3、官方玩家交流Q群：196133482";-----------------------------------------------------function p.ShowUI()	LogInfo( "ActivityNoticeUI: ShowUI" );	local scene = GetSMGameScene();	if not CheckP(scene) then	LogInfo( "ActivityNoticeUI: ShowUI failed! scene is nil" );		return false;	end		local layer = createNDUILayer();	if not CheckP(layer) then		LogInfo( "ActivityNoticeUI: ShowUI failed! layer is nil" );		return false;	end		layer:SetPopupDlgFlag( true );	layer:Init();	layer:SetTag( NMAINSCENECHILDTAG.ActivityNoticeUI );	layer:SetFrameRect( RectFullScreenUILayer );	scene:AddChildZ( layer, 1 );	local uiLoad = createNDUILoad();	if ( nil == uiLoad ) then		layer:Free();		LogInfo( "ActivityNoticeUI: ShowUI failed! uiLoad is nil" );		return false;	end	uiLoad:Load( "activity.ini", layer, p.OnUIEventActivityNoticeUI, 0, 0 );	uiLoad:Free();		--	local pLabelNotice = GetLabel( layer, ID_LABEL_NOTICE );	if ( pLabelNotice ~= nil ) then		pLabelNotice:SetText( szNotice );	end	end---------------------------------------------------function p.CloseUI()	local scene = GetSMGameScene();	if ( scene ~= nil ) then		scene:RemoveChildByTag( NMAINSCENECHILDTAG.ActivityNoticeUI, true );	endend---------------------------------------------------function p.OnUIEventActivityNoticeUI( uiNode, uiEventType, param )	local tag = uiNode:GetTag();	if ( uiEventType == NUIEventType.TE_TOUCH_BTN_CLICK ) then        if ( ID_BTN_CLOSE == tag ) then        	p.CloseUI();        end    end    return true;end