-----------------------------------------------------描述: 活动公告界面--时间: 2012.9.28--作者: Guosen---------------------------------------------------ActivityNoticeUI = {}local p = ActivityNoticeUI;---------------------------------------------------local ID_BTN_CLOSE			= 533;	-- Xlocal ID_LABEL_NOTICE		= 14;	-- 公告标签local szTitle	= "10月18号更新公告";local szNotice	= "1、征收新增功能：\n有几率获得金币。\n\n2、竞技场奖励：\n1-20名奖励各有不同，21-50名奖励差异更明显。\n\n3、BOSS战奖励：\n每次战斗，随机获得1-3级宝石奖励；击杀奖励获得4级宝石礼包；排名奖励获得2-5级宝石礼包。";-----------------------------------------------------function p.ShowUI()	LogInfo( "ActivityNoticeUI: ShowUI" );	local scene = GetSMGameScene();	if not CheckP(scene) then	LogInfo( "ActivityNoticeUI: ShowUI failed! scene is nil" );		return false;	end		local layer = createNDUILayer();	if not CheckP(layer) then		LogInfo( "ActivityNoticeUI: ShowUI failed! layer is nil" );		return false;	end	layer:Init();	layer:SetTag( NMAINSCENECHILDTAG.ActivityNoticeUI );	layer:SetFrameRect( RectFullScreenUILayer );	scene:AddChildZ( layer, 1 );	local uiLoad = createNDUILoad();	if ( nil == uiLoad ) then		layer:Free();		LogInfo( "ActivityNoticeUI: ShowUI failed! uiLoad is nil" );		return false;	end	uiLoad:Load( "activity.ini", layer, p.OnUIEventActivityNoticeUI, 0, 0 );	uiLoad:Free();		--	local pLabelNotice = GetLabel( layer, ID_LABEL_NOTICE );	if ( pLabelNotice ~= nil ) then		pLabelNotice:SetText( szNotice );	end	end---------------------------------------------------function p.CloseUI()	local scene = GetSMGameScene();	if ( scene ~= nil ) then		scene:RemoveChildByTag( NMAINSCENECHILDTAG.ActivityNoticeUI, true );	endend---------------------------------------------------function p.OnUIEventActivityNoticeUI( uiNode, uiEventType, param )	local tag = uiNode:GetTag();	if ( uiEventType == NUIEventType.TE_TOUCH_BTN_CLICK ) then        if ( ID_BTN_CLOSE == tag ) then        	p.CloseUI();        end    end    return true;end