-----------------------------------------------------描述: 玩家任务网络消息处理及其逻辑--时间: 2012.2.23--作者: jhzheng---------------------------------------------------MsgPlayerTask = {}local p = MsgPlayerTask;local TASK_DATA_ACTION_INVALID								= 0;local TASK_DATA_ACTION_UPDATE_CLIENT_TASK_LIST				= 1; --更新用户任务列表local TASK_DATA_ACTION_UPDATE_CLIENT_CANACCEPT_TASK_LIST	= 2; --更新可接列表local TASK_ACTION_INVALID									= 0;local TASK_ACTION_CLIENT_COMMIT								= 1; --客户端请求完成任务local TASK_ACTION_CLIENT_ACCEPT								= 2; --客户端请求接收任务local TASK_ACTION_CLIENT_DELETE								= 3; --客户端请求删除任务local TASK_ACTION_SERVER_COMMIT_SUCC						= 4; --服务端通知客户端任务完成处理成功local TASK_ACTION_SERVER_ACCEPT_SUCC						= 5; --服务端通知客户端任务接受处理成功		local TASK_ACTION_SERVER_DEL_SUCC							= 6; --服务端通知客户端任务放放弃处理成功		-- 网络消息处理(更新任务数据)function p.ProcessTaskDataAction(netdata)	local btTaskAction			= ConvertN(netdata:ReadByte());	local btCount				= ConvertN(netdata:ReadByte());	if btTaskAction == TASK_DATA_ACTION_UPDATE_CLIENT_TASK_LIST then		--更新用户任务列表		LogInfo("update task accept list count[%d]:", btCount);		TASK.DelGameData();		for	i=1, btCount do			local nTaskId		= ConvertN(netdata:ReadInt());			local nStateState	= ConvertN(netdata:ReadByte());			local data1			= ConvertN(netdata:ReadShort());			local data2			= ConvertN(netdata:ReadShort());			local data3			= ConvertN(netdata:ReadShort());			local data4			= ConvertN(netdata:ReadShort());			local data5			= ConvertN(netdata:ReadShort());			local data6			= ConvertN(netdata:ReadShort());						LogInfo(" [%d][%d][%d][%d][%d][%d][%d][%d]", nTaskId,						nStateState, data1, data2, data3, data4,						data5, data6);			TASK.CreateTask(nTaskId,nStateState,					data1, data2,					data3, data4,					data5, data6				);			--[[						LogInfo(" [%d]", nTaskId);			TASK.SetGameDataN(nTaskId, TASK_DATA.ID, nTaskId);			TASK.SetGameDataN(nTaskId, TASK_DATA.TASK_STATE, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA1, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA2, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA3, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA4, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA5, ConvertN(netdata:ReadShort()));			TASK.SetGameDataN(nTaskId, TASK_DATA.DATA6, ConvertN(netdata:ReadShort())); --]]		end		if IsUIShow(NMAINSCENECHILDTAG.PlayerTask) then			-- 玩家任务界面处理显示状态			TaskUI.RefreshTaskList(true);		end	elseif btTaskAction == TASK_DATA_ACTION_UPDATE_CLIENT_CANACCEPT_TASK_LIST then		--更新可接列表			LogInfo("update task unaccept list count[%d]:", btCount);		TASK.ClearCanAcceptTasks();		for i=1, btCount do			local nId = ConvertN(netdata:ReadInt());			TASK.AddCanAcceptTask(nId);			LogInfo(" [%d]", nId);		end				if IsUIShow(NMAINSCENECHILDTAG.PlayerTask) then			-- 玩家任务界面处理显示状态			TaskUI.RefreshTaskList(false);		end	end		return 1;end-- 任务交互function p.ProcessTaskAction(netdata)	local btTaskAction			= ConvertN(netdata:ReadByte());	local nTaskId				= ConvertN(netdata:ReadInt());	LogInfo("任务交互[%d][%d]", btTaskAction, nTaskId);	if  btTaskAction == TASK_ACTION_SERVER_COMMIT_SUCC  or		btTaskAction == TASK_ACTION_SERVER_DEL_SUCC then		LogInfo("recv finish task notify");		 --服务端通知客户端任务完成处理成功		 --删除任务		 TASK.DelTaskData(nTaskId);		 if IsUIShow(NMAINSCENECHILDTAG.PlayerTask) then			-- 玩家任务界面处理显示状态			TaskUI.DelAcceptTask(true,nTaskId );		end		CloseLoadBar();	elseif btTaskAction == TASK_ACTION_SERVER_ACCEPT_SUCC then		LogInfo("recv accept task notify");		 --服务端通知客户端任务接受处理成功		 TASK.CreateTask(nTaskId, TASK.SM_TASK_STATE.STATE_UNCOMPLETE, 					0, 0, 0, 0, 0, 0				);		 if IsUIShow(NMAINSCENECHILDTAG.PlayerTask) then			-- 玩家任务界面处理显示状态			TaskUI.AddTask(true, nTaskId);		end		_G.SysChat("您成功接受任务" .. TASK.GetTaskName(nTaskId));		CloseLoadBar();	end		return 1;endfunction p.SendTaskAction(nTaskId, nAction)	if not CheckN(nTaskId) or not CheckN(nAction) then		return false;	end	if nTaskId <= 0 then		return false;	end	local netdata = createNDTransData(NMSG_Type._MSG_TASK_ACTION);	if nil == netdata then		return false;	end	netdata:WriteByte(nAction);	netdata:WriteInt(nTaskId);	SendMsg(netdata);	netdata:Free();	LogInfo("send task[%d] action[%d]", nTaskId, nAction);	return true;endfunction p.SendCompleteTask(nTaskId)	return p.SendTaskAction(nTaskId, TASK_ACTION_CLIENT_COMMIT);endfunction p.SendAcceptTask(nTaskId)	return p.SendTaskAction(nTaskId, TASK_ACTION_CLIENT_ACCEPT);endfunction p.SendDelTask(nTaskId)	return p.SendTaskAction(nTaskId, TASK_ACTION_CLIENT_DELETE);endRegisterNetMsgHandler(NMSG_Type._MSG_TASK_ACTION, "p.ProcessTaskAction", p.ProcessTaskAction);RegisterNetMsgHandler(NMSG_Type._MSG_TASK_DATA_ACTION, "p.ProcessTaskDataAction", p.ProcessTaskDataAction);